svg.script:
// Erasing dice effect - (example in concept, would require more advanced coding for interactivity)
let dice = document.querySelectorAll('#dice rect');
let path = document.querySelector('path');

path.addEventListener('animationiteration', function() {
  // Erase the first dice on each iteration of the snake animation
  dice[0].setAttribute('fill', 'white'); // Set the dice to white (effectively "erased")
  dice = Array.from(dice).slice(1); // Remove the erased dice from the list
});
